package com.ionicframework.IdentityVault;

import android.app.Activity;
import android.os.Build;
import androidx.annotation.RequiresApi;
import androidx.appcompat.app.AppCompatActivity;

@RequiresApi(api = Build.VERSION_CODES.M)
public class CustomPasscodeVault extends VaultBase {

    public CustomPasscodeVault(IdentityVaultConfig config, AppCompatActivity activity) {
        super(config, activity);
        this.allowedInvalidUnlockAttempts = config.customPasscodeInvalidUnlockAttempts;
    }

    @Override
    protected String getVaultType() {
        return VaultType.CUSTOM_PASSCODE;
    }

    @Override
    public void unlock(boolean forceUnlock) throws VaultError {
        if (this.isLocked()) {
            boolean doesVaultExist = this.doesVaultExist();
            if (doesVaultExist || forceUnlock) {
                if (this.customPasscode == null) {
                    throw new MissingPasscodeError(!doesVaultExist);
                }
            }
        }
        super.unlock(forceUnlock);
    }
}
